SELECT "ART Number","Sex", "Current Age", CASE WHEN "Last Pregnancy Status"='Yes' THEN 'Pregnant' WHEN "Last Breast Feeding"='Yes' THEN 'Breast Feeding' ELSE NULL END AS "PBF Status",  "ART Start Date", "Months Since ART Start" as "ART Duration (months)", "TPT Regimen", "TPT Start Date", "TPT Expected Stop Date", "TPT Actual Stop Date", "TPT Adherance", "TPT Status", "TPT Stop Reason" as "Stop Reason" FROM analysis.fact_sentinel_event se INNER JOIN analysis.dim_age_group ag ON se."Current Age"=ag.age WHERE  "TX Curr" = 'Yes'