SELECT "ART Number","Sex", "Current Age", CASE WHEN "Last Pregnancy Status"='Yes' THEN 'Pregnant' WHEN "Last Breast Feeding"='Yes' THEN 'Breast Feeding' ELSE NULL END AS "PBF Status",  "ART Start Date", "Months Since ART Start" as "ART Duration (months)", "Last Visit Date","Last Next Visit Date" as "Next FUP Visit", "Last Viral Load Order Date" as "Last Viral Load Test Date", "Last Viral Load Result Date" as "Last Viral Load Result Date", "Last Viral Load Result Status" as "Result Status",  "Last Viral Load Result" as "VL Result",  "Last Viral Load Result Category" as "VL Status"  FROM analysis.fact_sentinel_event se INNER JOIN analysis.dim_age_group ag ON se."Current Age"=ag.age WHERE "TX Curr" = 'Yes'